mapper.insert:
INSERT INTO tokens (
    id, user_id, provider_id, value, alias, weight, enabled, healthy,
    last_used, encryption_version, value_hash, created_at, updated_at
)
VALUES (
    #{id}, #{userId}, #{providerId}, #{value}, #{alias}, #{weight}, #{enabled}, #{healthy},
    #{lastUsed}, #{encryptionVersion}, #{valueHash}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE tokens
SET healthy = #{healthy,jdbcType=BOOLEAN}, updated_at = NOW()
WHERE id = #{id,jdbcType=VARCHAR}

UPDATE tokens
SET last_used = NOW(), updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM tokens
WHERE provider_id = #{providerId}

SELECT COUNT(*) > 0
FROM tokens
WHERE provider_id = #{providerId,jdbcType=VARCHAR}
  AND alias = #{alias,jdbcType=VARCHAR}
  AND id != #{excludeId,jdbcType=VARCHAR}

SELECT COUNT(*) > 0
FROM tokens
WHERE provider_id = #{providerId,jdbcType=VARCHAR}
  AND alias = #{alias,jdbcType=VARCHAR}

SELECT COUNT(*) > 0
FROM tokens
WHERE user_id = #{userId,jdbcType=BIGINT}
  AND value_hash = #{tokenValueHash,jdbcType=VARCHAR}

SELECT COUNT(*) > 0
FROM tokens
WHERE user_id = #{userId,jdbcType=BIGINT}
  AND value_hash = #{tokenValueHash,jdbcType=VARCHAR}
  AND id != #{excludeId,jdbcType=VARCHAR}

mapper.update:
UPDATE tokens
SET value_hash = #{tokenHash,jdbcType=VARCHAR}, updated_at = NOW()
WHERE id = #{id,jdbcType=VARCHAR}
